# --- C√ìDIGO DO √çCONE (BASE64) ---
ICONE_VASSOURA_BASE64 = """


"""

# --- BANCO DE DADOS DE TAGS COMUNS ---
PRESETS = {
    "V√≠deos": ".mp4, .mkv, .avi, .mov, .wmv, .flv",
    "Fotos": ".jpg, .jpeg, .jfif, .png, .gif, .webp, .svg, .bmp",
    "M√∫sicas": ".mp3, .wav, .flac, .ogg, .m4a",
    "Documentos": ".pdf, .docx, .txt, .xlsx, .pptx, .csv",
    "Compactados": ".zip, .rar, .7z, .tar, .gz, .bz2",
    "Instaladores/Sist": ".exe, .msi, .iso, .msu, .bat, .jar"
}

TODAS_EXTENSOES = []
for lista in PRESETS.values():
    TODAS_EXTENSOES.extend([ext.strip() for ext in lista.split(',')])
TODAS_EXTENSOES = list(set(TODAS_EXTENSOES))

regras_usuario = {}


# --- FUN√á√ïES DE L√ìGICA ---

def auto_scan():
    caminho = lbl_caminho.cget("text")
    if not os.path.exists(caminho) or caminho == "Nenhuma pasta selecionada":
        messagebox.showwarning("Aviso", "Selecione uma pasta alvo primeiro!")
        return

    arquivos = [f for f in os.listdir(caminho) if os.path.isfile(os.path.join(caminho, f))]
    if not arquivos:
        messagebox.showinfo("Auto-Scan", "A pasta est√° vazia!")
        return

    novas_regras = {}

    for arquivo in arquivos:
        ext = os.path.splitext(arquivo)[1].lower()
        if not ext: continue

        achou_categoria = False
        for categoria, exts_preset in PRESETS.items():
            if ext in [e.strip() for e in exts_preset.split(',')]:
                nome_pasta = f"ORG_{categoria}"
                if nome_pasta not in novas_regras: novas_regras[nome_pasta] = []
                if ext not in novas_regras[nome_pasta]: novas_regras[nome_pasta].append(ext)
                achou_categoria = True
                break

        if not achou_categoria:
            if "ORG_Outros" not in novas_regras: novas_regras["ORG_Outros"] = []
            if ext not in novas_regras["ORG_Outros"]: novas_regras["ORG_Outros"].append(ext)

    # Atualiza a interface e o dicion√°rio global
    regras_usuario.clear()
    list_regras.delete(0, END)
    for pasta, lista_ext in novas_regras.items():
        regras_usuario[pasta] = lista_ext
        list_regras.insert(END, f"üìÅ {pasta} ({len(lista_ext)} tipos)")

    messagebox.showinfo("Auto-Scan", "Escaneamento conclu√≠do! As regras sugeridas foram adicionadas.")


def ajuda_auto_scan():
    msg = (
        "O que o AUTO-SCAN faz?\n\n"
        "1. Analisa todos os arquivos da pasta selecionada.\n"
        "2. Identifica automaticamente as extens√µes.\n"
        "3. Organiza em pastas (Ex: V√≠deos, Fotos) conforme nossos presets.\n"
        "4. Extens√µes desconhecidas v√£o para a pasta 'ORG_Outros'.\n\n"
        "Dica: Voc√™ pode editar ou remover as regras geradas antes de executar a faxina!"
    )
    messagebox.showinfo("Ajuda: Auto-Scan", msg)


def autocompletar(event):
    if event.keysym in ("BackSpace", "Left", "Right", "Up", "Down", "Shift_L", "Shift_R", "Control_L", "Return"):
        return
    texto_completo = ent_exts.get().lower()
    partes = texto_completo.split(',')
    ultima_parte = partes[-1].strip() if partes else ""
    if len(ultima_parte) < 1: return
    busca = ultima_parte if ultima_parte.startswith('.') else f".{ultima_parte}"
    for sugestao in TODAS_EXTENSOES:
        if sugestao.startswith(busca):
            posicao_cursor = ent_exts.index("insert")
            inicio_da_tag = posicao_cursor - len(ultima_parte)
            if not ultima_parte.startswith('.'):
                ent_exts.delete(inicio_da_tag, posicao_cursor)
                ent_exts.insert(inicio_da_tag, sugestao)
                ent_exts.selection_range(inicio_da_tag + len(ultima_parte) + 1, END)
            else:
                restante = sugestao[len(ultima_parte):]
                ent_exts.insert(posicao_cursor, restante)
                ent_exts.selection_range(posicao_cursor, END)
            break


def selecionar_pasta():
    pasta = filedialog.askdirectory()
    if pasta: lbl_caminho.config(text=f"{pasta}", fg="#2ecc71")


def carregar_preset(categoria):
    ent_pasta.delete(0, END)
    ent_exts.delete(0, END)
    ent_pasta.insert(0, f"ORG_{categoria}")
    ent_exts.insert(0, PRESETS[categoria])


def processar_tag_enter(event):
    texto_bruto = ent_exts.get().lower()
    if not texto_bruto: return
    texto_padronizado = texto_bruto.replace('.', ',').replace(' ', ',')
    tags = [f".{t.strip()}" for t in texto_padronizado.split(',') if t.strip()]
    novo_texto = ", ".join(tags)
    if novo_texto:
        ent_exts.delete(0, END)
        ent_exts.insert(0, f"{novo_texto}, ")


def adicionar_regra():
    nome_pasta = ent_pasta.get().strip()
    exts_bruto = ent_exts.get().strip().lower()
    if nome_pasta and exts_bruto:
        limpeza = exts_bruto.replace('.', ',').replace(' ', ',')
        lista_exts = [f".{t.strip()}" for t in limpeza.split(',') if t.strip()]
        if nome_pasta in regras_usuario:
            for i, item in enumerate(list_regras.get(0, END)):
                if item.startswith(f"üìÅ {nome_pasta}"):
                    list_regras.delete(i)
                    break
        regras_usuario[nome_pasta] = lista_exts
        list_regras.insert(END, f"üìÅ {nome_pasta} ({len(lista_exts)} tipos)")
        ent_pasta.delete(0, END)
        ent_exts.delete(0, END)
    else:
        messagebox.showwarning("Aviso", "Preencha os campos!")


def carregar_para_edicao(event):
    selecao = list_regras.curselection()
    if selecao:
        item_texto = list_regras.get(selecao)
        nome_pasta = item_texto.replace("üìÅ ", "").split(" (")[0]
        if nome_pasta in regras_usuario:
            ent_pasta.delete(0, END)
            ent_pasta.insert(0, nome_pasta)
            ent_exts.delete(0, END)
            ent_exts.insert(0, ", ".join(regras_usuario[nome_pasta]))


def remover_regra():
    selecao = list_regras.curselection()
    if selecao:
        item_texto = list_regras.get(selecao)
        nome_pasta_limpo = item_texto.replace("üìÅ ", "").split(" (")[0]
        if nome_pasta_limpo in regras_usuario: del regras_usuario[nome_pasta_limpo]
        list_regras.delete(selecao)
        ent_pasta.delete(0, END)
        ent_exts.delete(0, END)


def iniciar_organizacao():
    caminho = lbl_caminho.cget("text")
    if not os.path.exists(caminho) or caminho == "Nenhuma pasta selecionada":
        messagebox.showwarning("Aviso", "Selecione a pasta alvo!")
        return
    if not regras_usuario:
        messagebox.showwarning("Aviso", "Adicione regras!")
        return
    cont = 0
    lista_arquivos = os.listdir(caminho)
    for arquivo in lista_arquivos:
        origem = os.path.join(caminho, arquivo)
        if os.path.isdir(origem) or not os.path.exists(origem): continue
        ext = os.path.splitext(arquivo)[1].lower()
        for pasta, lista in regras_usuario.items():
            if ext in lista:
                dest_dir = os.path.join(caminho, pasta)
                os.makedirs(dest_dir, exist_ok=True)
                nome_final = arquivo
                base, extension = os.path.splitext(arquivo)
                i = 1
                while os.path.exists(os.path.join(dest_dir, nome_final)):
                    nome_final = f"{base} ({i}){extension}"
                    i += 1
                try:
                    shutil.move(origem, os.path.join(dest_dir, nome_final))
                    cont += 1
                    break
                except:
                    continue
    messagebox.showinfo("Sucesso", f"Faxina conclu√≠da! {cont} arquivos movidos.")


